<?php require_once('../config.php'); require_once(PATH_ROOT_CLASES . 'ccategoria.php'); require_once(PATH_ROOT_CLASES . 'cdatabase.php'); require_once(PATH_ROOT_CLASES . 'cutils.php');  $action = Cutils::get_filtered_params('a', 1, 0, 1, 0); //Recojemos el id del usuario a verif ( isset( $_REQUEST['id']) )	$id  = Cutils::get_filtered_params('id', 1, 0, 1, 0);else{	$stripped = Cutils::get_filtered_params('stripped', 1, 0, 1, 0);	$id = Ccategoria::get_id_from_stripped($stripped);}//Miramos si existe. Si no existe enviamos a list$categoria = new Ccategoria($id);if (!$categoria->exists()){	Cutils::set_web_information(0, 'El item no existe o ha sido eliminado de la base de datos');	Clocation::header_location('/admin/categoria/list/');}if ($action == 'delete'){	$item = new Ccategoria($id);	$result = $item->delete();		if ($result){			$info_ok = 1; 			$info_msg = 'Eliminado correctamente'; 			Cutils::set_web_information($info_ok, $info_msg); 			Clocation::header_location('/admin/categoria/list/'); 		}else{			$info_ok = 0; 			$info_msg = 'Error eliminando'; 			Cutils::set_web_information($info_ok, $info_msg); 			Clocation::header_location('/admin/categoria/list/'); 		}	}?>