<?php require_once('../config.php'); require_once(PATH_ROOT_CLASES . 'ccategoria_gallery.php'); require_once(PATH_ROOT_CLASES . 'cform_construct_categoria_gallery.php'); require_once(PATH_ROOT_CLASES . 'cpagelayout_backend.php'); require_once(PATH_ROOT_CLASES . 'ccategoria.php'); require_once(PATH_ROOT_CLASES . 'cutils.php');  // Definimos las seccins del layout $names_section = array( 	'top' 			=> 	'vtop', 	'menu' 			=> 	'vmenu',  	'main' 			=> 	'vcategoria_gallery_create', 	'footer'		=>	'vfooter' );if ( isset( $_REQUEST['categoria_id']) or isset( $_REQUEST['cformcategoria_gallery_categoria_id'] ) ){ 	$id  = Cutils::get_filtered_params('categoria_id', 1, 0, 1, 0); 	$return  =  $id;}else if ( isset( $_REQUEST['stripped']) ){ 	$stripped = Cutils::get_filtered_params('stripped', 1, 0, 1, 0); 	$id = Ccategoria::get_id_from_stripped($stripped); 	$return = $stripped; } //incluimos el formulario de usuarios y lo aÃ±adimos al array de formularios$newform = new Cform_construct_categoria_gallery('cformcategoria_gallery','es','new','', $id, $return);$array_forms['cformcategoria_gallery'] = $newform; //incluimos la validacion por javascript$heredoc = Cutils::get_scripts_heredoc_form_validation($array_forms);$layout	= new Cpagelayout_backend( $names_section ); 	$layout->set_page_link_blocker(true);	$layout->set_page_forms($array_forms);	$layout->set_page_heredoc($heredoc);	$layout->set_var('form_type', 'new');	$layout->set_var('return', $return);	$layout->set_var('rid', $id);$layout->Display();?>