CREATE TABLE blog (id INT(9) NOT NULL AUTO_INCREMENT PRIMARY KEY ,titulo_es varchar(60) DEFAULT '' NOT NULL, titulo_ca varchar(60) DEFAULT '' NOT NULL, titulo_en varchar(60) DEFAULT '' NOT NULL, descripcion_es text  , descripcion_ca text  , descripcion_en text  , image  varchar(200)  DEFAULT '' , stripped varchar(200)  DEFAULT '' , created TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,updated TIMESTAMP NOT NULL);