CREATE TABLE categoriera (id INT(9) NOT NULL AUTO_INCREMENT PRIMARY KEY ,name  varchar(60)  DEFAULT '' NOT NULL, cantidad INT(9) NOT NULL, publico INT(9) , idioma VARCHAR(50) NOT NULL, tipo VARCHAR(50) , descripcion text  , image  varchar(200)  DEFAULT '' , file  varchar(200)  DEFAULT '' , stripped varchar(200)  DEFAULT '' , created TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,updated TIMESTAMP NOT NULL);