CREATE TABLE categoria (id INT(9) NOT NULL AUTO_INCREMENT PRIMARY KEY ,name_es varchar(60) DEFAULT '' NOT NULL, name_ca varchar(60) DEFAULT '' NOT NULL, name_en varchar(60) DEFAULT '' NOT NULL, descripcion text  , stripped_es varchar(500)  DEFAULT '' , stripped_ca varchar(500)  DEFAULT '' , stripped_en varchar(500)  DEFAULT '' , created TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,updated TIMESTAMP NOT NULL);