CREATE TABLE categoria_gallery (id INT(9) NOT NULL AUTO_INCREMENT PRIMARY KEY ,name_es varchar(60) DEFAULT '' NOT NULL, name_ca varchar(60) DEFAULT '' NOT NULL, name_en varchar(60) DEFAULT '' NOT NULL, categoria_id  varchar(200)  DEFAULT '' NOT NULL, stripped_es varchar(200)  DEFAULT '' NOT NULL, stripped_ca varchar(200)  DEFAULT '' NOT NULL, stripped_en varchar(200)  DEFAULT '' NOT NULL, created TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,updated TIMESTAMP NOT NULL);