<?php require_once('config.php'); require_once(PATH_ROOT_CLASES . 'ccategoria.php'); require_once(PATH_ROOT_CLASES . 'cpagelayout_frontend.php'); require_once(PATH_ROOT_CLASES . 'cutils.php'); require_once(PATH_ROOT_CLASES . 'cimagen.php'); //#NO-BORRAR#//// Definimos las seccins del layout $names_section = array( 	'top' 			=> 	'vtop', 	'menu' 			=> 	'vmenu',  	'main' 			=> 	'vcategoria', 	'footer'		=>	'vfooter' );//Recojemos el id del usuario a verif ( isset( $_REQUEST['id']) )	$id  = Cutils::get_filtered_params('id', 1, 0, 1, 0);else{	$stripped = Cutils::get_filtered_params('stripped', 1, 0, 1, 0);	$id = Ccategoria::get_id_from_stripped($stripped);}//Miramos si existe. Si no existe enviamos a list$categoria = new Ccategoria($id);if (!$categoria->exists()){	Cutils::set_web_information(0, 'El item no existe o ha sido eliminado de la base de datos');	Clocation::header_location('/categoria/list/');}$layout	= new Cpagelayout_frontend( $names_section ); 	$item = new Ccategoria($id);	$layout->set_var('item', $item->load_item_to_array());	//#NO-BORRAR2#//$layout->Display();?>