<?php require_once(PATH_ROOT_CLASES . 'cdatabase.php'); require_once(PATH_ROOT_CLASES . 'cpaginado.php');  require_once(PATH_ROOT_CLASES . 'cform_construct.php');  class Cform_construct_categoria extends  Cform_construct {	var $m_item_id;	function Cform_construct_categoria($form_name = '', $lng = 'es', $type = 'new', $item_id = ''){		$this->m_item_id = $item_id;		Cform_construct_categoria::Cform_construct($form_name, $lng, $type); 	}	function get_item_id(){		return $this->m_item_id; 	} 	function populate($fillbd = true){		$this->m_form_object = new Cform($this->get_form_name(), $this->get_form_name(), htmlentities($_SERVER['PHP_SELF'], ENT_QUOTES), 'post'); 		$obj = new Cform_text('name',$this->get_form_name().'_name', '', '', '', 1, 'text', 1, 60, 60);		$this->m_form_object->add_inputs($obj,$obj->get_id());		if ($this->m_type == 'new'){			$obj = new Cform_doc('dfas',$this->get_form_name().'_dfas', '', '', '', '0', 'pdf,doc');			$this->m_form_object->add_inputs($obj,$obj->get_id());		}else{			$obj = new Cform_doc('dfas',$this->get_form_name().'_dfas', '', '', '', '0', 'pdf,doc');			$this->m_form_object->add_inputs($obj,$obj->get_id());		}		if ($this->m_type == 'new'){			$obj = new Cform_image('image_es',$this->get_form_name().'_image_es', '', '', '', '0');			$this->m_form_object->add_inputs($obj,$obj->get_id());		}else{			$obj = new Cform_image('image_es',$this->get_form_name().'_image_es', '', '', '', '0');			$this->m_form_object->add_inputs($obj,$obj->get_id());		}		if ($this->m_type == 'new'){			$obj = new Cform_image('image_ca',$this->get_form_name().'_image_ca', '', '', '', '0');			$this->m_form_object->add_inputs($obj,$obj->get_id());		}else{			$obj = new Cform_image('image_ca',$this->get_form_name().'_image_ca', '', '', '', '0');			$this->m_form_object->add_inputs($obj,$obj->get_id());		}		if ($this->m_type == 'new'){			$obj = new Cform_image('image_en',$this->get_form_name().'_image_en', '', '', '', '0');			$this->m_form_object->add_inputs($obj,$obj->get_id());		}else{			$obj = new Cform_image('image_en',$this->get_form_name().'_image_en', '', '', '', '0');			$this->m_form_object->add_inputs($obj,$obj->get_id());		}		if ($this->m_type == 'edit'){			$obj = new Cform_hidden('item_id', $this->get_form_name().'_item_id', '', '', $this->m_item_id); 			$this->m_form_object->add_inputs($obj,$obj->get_id()); 		}		 //creacion token seguretat (anti csrf) 		$this->create_token();		$obj = new Cform_button_submit('submit',$this->get_form_name().'_submit','Enviar'); 		$this->m_form_object->add_inputs($obj,$obj->get_id()); 		$this->m_form_object->close_form(); 		// si es un edit llenamos los objetos con los campos de la base de datos		if ($this->get_type() == 'edit' and $fillbd){			$this->search_and_fill_object_from_bd();		}	} 	function process(){		if (!isset($_POST[$this->m_form_name.'_submit'])){			$this->populate();		}else{			//valida token antiCSRF			$this->validate_token();			//Creamos el formulario y le asignamos los valores			$this->get_and_fill_submited_params();			//validamos el formulario			if ($this->validate()){				//obtenemos los valores del formulario para hacer el save				$item = $this->fill_array_to_save();				//Creamos el objeto file				$archivo = new Carchivo($this->get_form_name(), 'file', 'dfas', 'categoria');				$ruta_archivo = $archivo->get_url_photo_to_save();				//Creamos los objeto imagen				$image_es = new Cimagen($this->get_form_name(), 'image', 'image_es', 'categoria');				$ruta_img_es = $image_es->get_url_photo_to_save();				$image_ca = new Cimagen($this->get_form_name(), 'image', 'image_ca', 'categoria');				$ruta_img_ca = $image_ca->get_url_photo_to_save();				$image_en = new Cimagen($this->get_form_name(), 'image', 'image_en', 'categoria');				$ruta_img_en = $image_en->get_url_photo_to_save();				if ($this->m_type == 'new'){					//creamos el objeto cusers					$newitem = new Ccategoria('', $item['name'], $ruta_archivo, $ruta_img_es, $ruta_img_ca, $ruta_img_en);					//Si es un stripped hemos de mirar ke no ete creado ya en la BD					if (! $newitem->is_unique($item['name'])){						$this->set_info_action_form_failed('Ya hay un item creado con estos datos', 0); 						Clocation::header_location('/admin/categoria/list/');						exit(); 					}					// guardamos el objeto					if ($newitem->save()){						//Subimos el archivo						$result = $archivo->move();						//Subimos la imagen y creamos el thumb						$result = $image_es->move();						if ($result['correct'])							$image_es->do_thumbnail();						$result = $image_ca->move();						if ($result['correct'])							$image_ca->do_thumbnail();						$result = $image_en->move();						if ($result['correct'])							$image_en->do_thumbnail();						$this->set_info_action_form_success('Item guardado correctamente', 0); 					}else {						$this->set_info_action_form_failed('Error creando el item', 0); 					}					Clocation::header_location('/admin/categoria/list/');					exit(); 				}else if ($this->m_type == 'edit'){//fi type==new					//Si hay un archivo borramos el anterior					if ($ruta_archivo){						Carchivo::delete(Carchivo::get_ruta_from_database('categoria', $item['item_id'], 'dfas'));					}					//Si hay una imagen nueva borramos la anterior					if ($ruta_img_es){						Cimagen::delete(Cimagen::get_ruta_from_database('categoria', $item['item_id'], 'image_es'));					}					//Si hay una imagen nueva borramos la anterior					if ($ruta_img_ca){						Cimagen::delete(Cimagen::get_ruta_from_database('categoria', $item['item_id'], 'image_ca'));					}					//Si hay una imagen nueva borramos la anterior					if ($ruta_img_en){						Cimagen::delete(Cimagen::get_ruta_from_database('categoria', $item['item_id'], 'image_en'));					}					//Obtenemos el objeto					$edititem = new Ccategoria($item['item_id']);					$edititem->edit( $item['name'], $ruta_archivo, $ruta_img_es, $ruta_img_ca, $ruta_img_en);					// editamos el objeto					if ($edititem){						//Subimos el archivo						$result = $archivo->move();						//Subimos la imagen y creamos el thumb						$result = $image_es->move();						if ($result['correct'])							$image_es->do_thumbnail();						$result = $image_ca->move();						if ($result['correct'])							$image_ca->do_thumbnail();						$result = $image_en->move();						if ($result['correct'])							$image_en->do_thumbnail();						$this->set_info_action_form_success('Item editado correctamente', 0); 					}else {						$this->set_info_action_form_failed('Error editando el item', 0); 					}					Clocation::header_location('/admin/categoria/list/');					exit(); 				}//fi else type='edit'			}//fi validate		}//fi else _post	}//fin process	function search_and_fill_object_from_bd(){		// Buscamos en la base de datos los usuarios		$item = new Ccategoria($this->get_item_id()); 		$resultat = $item->load_item_to_array(); 		// Llenamos el objeto formulario con los valores de la bd		$this->fill_object_from_bd($resultat);	}//fin search_and_fill}?>