<?php $items = $this->get_var('item'); $paginate = $this->get_var('paginate'); $return = $this->get_var('return'); echo $this->get_web_information(); echo "<h1>".$this->translate('list_of', array('categoria_gallery'))."</h1>"; if ($items){	echo "<table class='formattable'>";  		echo "<thead>"; 			echo "<th>Name</th> "; 			echo "<th>Categoria_id</th> "; 			echo "<th colspan='3'>".$this->translate('options')."</th>"; 		echo "</thead>"; 		echo "<tbody>"; 		foreach ($items as $item) { 			echo "<tr>"; 				echo "<td>".$item['name_'.Cutils::get_actual_lng()]."</td>"; 				echo "<td>".$item['categoria_id']."</td>"; 				echo "<td><a href='/admin/categoria_gallery/$return/".$item['stripped_'.Cutils::get_actual_lng()]."/'>".$this->translate('view')."</a></td> "; 				echo "<td><a href='/admin/categoria_gallery/edit/$return/".$item['stripped_'.Cutils::get_actual_lng()]."/'>".$this->translate('edit')."</a></td> "; 				echo "<td><a href='/admin/categoria_gallery/delete/$return/".$item['id']."/' onclick=\"return confirm('".$this->translate('confirm_delete')."');\">".$this->translate('delete')."</a></td> "; 			echo "</tr>"; 		} 		echo "</tbody>"; 	echo "</table>"; 	echo $paginate; 	echo "<br /><br /> "; 	echo "<a href='/admin/categoria_gallery/create/$return/'>".$this->translate('new_item')."</a>"; }else	echo "".$this->translate('no_items')." <a href='/admin/categoria_gallery/create/$return/'>".$this->translate('new_item')."</a>"; 	echo " <br><br><a href='/admin/categoria/list/'>".$this->translate('return_to', array('categoria'))." </a>"; ?>