<?php $items = $this->get_var('item'); $paginate = $this->get_var('paginate'); echo $this->get_web_information(); echo "<h1>".$this->translate('list_of', array('categoria'))."</h1>"; if ($items){	echo "<table class='formattable'>";  		echo "<thead>"; 			echo "<th>Name</th> "; 			echo "<th>Descripcion</th> "; 			echo "<th colspan='4'>".$this->translate('options')."</th>"; 		echo "</thead>"; 		echo "<tbody>"; 		foreach ($items as $item) { 			echo "<tr>"; 				echo "<td>".$item['name_'.Cutils::get_actual_lng()]."</td>"; 				echo "<td>".$item['descripcion']."</td>"; 				echo "<td><a href='/admin/categoria_gallery/list/".$item['stripped_'.Cutils::get_actual_lng()]."/'>Link a categoria_gallery (".Ccategoria_gallery::count_categoria_gallery($item['id']).")</a></td>";
 //#NO-BORRAR#// 				echo "<td><a href='/admin/categoria/".$item['stripped_'.Cutils::get_actual_lng()]."/'>".$this->translate('view')."</a></td> "; 				echo "<td><a href='/admin/categoria/edit/".$item['stripped_'.Cutils::get_actual_lng()]."/'>".$this->translate('edit')."</a></td> "; 				echo "<td><a href='/admin/categoria/delete/".$item['id']."/' onclick=\"return confirm('".$this->translate('confirm_delete')."');\">".$this->translate('delete')."</a></td> "; 			echo "</tr>"; 		} 		echo "</tbody>"; 	echo "</table>"; 	echo $paginate; 	echo "<br /><br /> "; 	echo "<a href='/admin/categoria/create/'>".$this->translate('new_item')."</a>"; }else	echo "".$this->translate('no_items')." <a href='/admin/categoria/create/'>".$this->translate('new_item')."</a>"; ?>