<?php $item = $this->get_var('item');$relation_list = $this->get_var('relation_list');
echo "<h1>Ver categoria</h1>"; echo "<b>Name_".Cutils::get_actual_lng()." :</b> ".$item['name_'.Cutils::get_actual_lng()]." <br /><br /> "; echo "<b>Descripcion :</b> ".$item['descripcion']." <br /><br /> "; echo "<b>Created :</b> ".$item['created']." <br /><br />"; echo "<b>Updated :</b> ".$item['updated']." <br /><br />"; echo "<h2>Relaciones</h2>";if ($relation_list['total']){ 	echo "Total de relaciones: ".$relation_list['total']."<br/>"; 	foreach ($relation_list['item'] as $relation){ 		echo  $relation['name_'.Cutils::get_actual_lng()] ." -- ";		echo  $relation['categoria_id']." -- ";		echo "<br />" ;	}}else{	echo "No existen relaciones.<br/>";}echo "<br /> <a href='/categoria/list/'>".$this->translate('return')."</a>";  ?>