<?php $items = $this->get_var('item'); $paginate = $this->get_var('paginate'); echo $this->get_web_information(); echo "<h1>".$this->translate('list_of', array('categoria'))."</h1>"; if ($items){	echo "<table class='formattable'>";  		echo "<thead>"; 			echo "<th>Image</th> "; 			echo "<th>Name</th> "; 			echo "<th>Dfas</th> "; 			echo "<th colspan='3'>".$this->translate('options')."</th>"; 		echo "</thead>"; 		echo "<tbody>"; 		foreach ($items as $item) { 			echo "<tr>"; 				echo "<td>".Carchivo::show_file($item['dfas'])."</td>"; 				echo "<td>".Cimagen::show_thumbnail($item['image_'.Cutils::get_actual_lng()])."</td>"; 				echo "<td>".$item['name']."</td>"; 				//#NO-BORRAR#//				echo "<td><a href='/categoria/".$item['stripped']."/'>".$this->translate('view')."</a></td> "; 				echo "<td><a href='/categoria/edit/".$item['stripped']."/'>".$this->translate('edit')."</a></td> "; 				echo "<td><a href='/categoria/delete/".$item['id']."/' onclick=\"return confirm('".$this->translate('confirm_delete')."');\">".$this->translate('delete')."</a></td> "; 			echo "</tr>"; 		} 		echo "</tbody>"; 	echo "</table>"; 	echo $paginate; 	echo "<br /><br /> "; 	echo "<a href='/categoria/create/'>".$this->translate('new_item')."</a>"; }else	echo "".$this->translate('no_items')." <a href='/categoria/create/'>".$this->translate('new_item')."</a>"; 	echo "<br/><br/><a href='/'>Volver al inicio</a>"?>